$("#loginForm").submit(function(a){a.preventDefault();let e=$("#loginForm").serialize();$.post(baseUrl+"API/CMSLogin",e,function(a){"wrong_password"==a.message?swal({title:"Login Gagal",text:"Password salah, mohon diperiksa kembali",type:"warning"}):"no_user"==a.message?swal({title:"Login Gagal",text:"Nama pengguna tidak ditemukan, mohon diperiksa kembali",type:"warning"}):swal({title:"Login Berhasil!",text:"Selamat datang, "+a.user,type:"success",buttons:{confirm:{className:"btn btn-success"}}}).then(a=>{a&&window.location.replace(baseUrl+"cms/dashboard")})})});